import React from 'react';
import recipes from '../recipes';

const Menu = () => {
  return (
    <div className='menu-container'>
      <div className='menu-header'>
        <h2>Today's Special !</h2>
        <button>Order Menu</button>
      </div>

      <div>
        {
          recipes.map(recipe => <div key={recipe.id}>
            <h2>{recipe.title}</h2>
          </div>)
        }
      </div>
    </div>
  );
};

export default Menu;
